  // const storageEmail = JSON.parse(localStorage.getItem('user'));
  // return storageEmail === undefined ? '' : storageEmail.email;
  // };
  const showAlert = () => alert('Sua busca deve conter somente 1 (um) caracter');
  const showAlertNotFound = () => alert('Sinto muito, não encontramos nenhuma receita para esses filtros.');
  const [userEmail, setUserEmail] = useState('');
  const [searchRender, setSearchRender] = useState(false);
  const [filterSearch, setFilterSearch] = useState('');
  const [recipesFilters, setRecipesFilters] = useState([]);
  const [searchInput, setSearchInput] = useState('');
  const [getSearchBtn, setSearchBtn] = useState(false);




  const checkRecipe = () => {
    if (recipesFilters === null || recipesFilters.length === 0) {
      showAlertNotFound();
    }
  };

  useEffect(
    checkRecipe,
    [getSearchBtn],
  );

  useEffect(() => {
    if (recipesFilters !== null) {
      if (recipesFilters.length === 1) {
        const lastHIstory = history.location.pathname;
        const zero = 0;
        let id;
        if (lastHIstory === '/bebidas') {
          const { idDrink } = recipesFilters[zero];
          id = idDrink;
        } else {
          const { idMeal } = recipesFilters[zero];
          id = idMeal;
        }
        history.push(`${lastHIstory}/${id}`);
      }
    }
  }, [recipesFilters]);

  useEffect(requestApi,
    [getSearchBtn]);

  const context = {
    filterSearch,
    userEmail,
    searchRender,
    searchInput,
    getSearchBtn,
    recipesFilters,
    isFetching,
    setSearchBtn,
    setSearchInput,
    setFilterSearch,
    setUserEmail,
    setSearchRender,
  };

  return (
    <RecipesContext.Provider value={context}>
      {children}
    </RecipesContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
