import React, { useState } from 'react';
import perfilIcon from '../images/profileIcon.svg';
import { Link } from 'react-router-dom';
import share from '../images/shareIcon.svg';
import copy from 'clipboard-copy';
import { ZERO, TWO_THOUSAND } from '../services/helpers';

function RecipesMade() {
    const recipesMade = JSON.parse(localStorage.getItem('doneRecipes'));
    const TWO = 2;
    const [copyText, setCopyText] = useState('');

    const handleCopyClick = (food) => {
        copy(`http://localhost:3000/${food.type}s/${food.id}`);
        setCopyText('Link copiado!');
        setInterval(() => setCopyText(''), TWO_THOUSAND);
      };

    return (
        <div>
            <header>
                <h1 data-testid="page-title">
                    Receitas Feitas
        </h1>
                <Link to="/perfil">
                    <button type="button">
                        <img
                            data-testid="profile-top-btn"
                            src={perfilIcon}
                            alt="perfil"
                        />
                    </button>
                </Link>
            </header>
            <div>
                <button data-testid="filter-by-all-btn" >All</button>
                <button data-testid="filter-by-food-btn" >Food</button>
                <button data-testid="filter-by-drink-btn" >Drinks</button>
            </div>
            {recipesMade ? recipesMade.map((recipe, index) => {
                if (recipe.type === 'comida') {
                    return (
                        <div key={index} >
                            <img width="200px" data-testid={`${index}-horizontal-image`} alt="recipe" src={recipe.image} />
                            <button onClick={() => handleCopyClick(recipe)} >
                                <img data-testid={`${index}-horizontal-share-btn`} alt="share" src={share} />
                            </button>
                            <p>{copyText}</p>
                            <p data-testid={`${index}-horizontal-name`} >{recipe.name}</p>
                            <p data-testid={`${index}-horizontal-top-text`} >{`${recipe.area} - ${recipe.category}`}</p>

                            <p data-testid={`${index}-horizontal-done-date`} >{recipe.doneDate}</p>
                            <p>
                                {recipe.tags.slice(ZERO, TWO).map((tag, indexTag) => <p data-testid={`${index}-${tag}-horizontal-tag`} key={indexTag} >{tag}</p>)}
                            </p>
                        </div>

                    )
                } else {
                    return (
                        <div key={index} >
                            <img width="200px" data-testid={`${index}-horizontal-image`} alt="recipe" src={recipe.image} />
                            <button onClick={() => handleCopyClick(recipe.id, recipe.type)}  >
                                <img data-testid={`${index}-horizontal-share-btn`} alt="share" src={share} />
                            </button>
                            <p>{copyText}</p>
                            <p data-testid={`${index}-horizontal-top-text`} >{recipe.alcoholicOrNot}</p>

                            <p data-testid={`${index}-horizontal-name`} >{recipe.name}</p>

                            <p data-testid={`${index}-horizontal-done-date`} >{recipe.doneDate}</p>
                        </div>
                    )
                }
            }) : null}

        </div>

    )
}

export default RecipesMade;
