import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import RecipesContext from './RecipesContext';
import history from '../history/history';
import useFetch from '../hooks/useFetch'
function Provider({ children }) {
  // const retrieveEmail = () => {
  // const storageEmail = JSON.parse(localStorage.getItem('user'));
  // return storageEmail === undefined ? '' : storageEmail.email;
  // };
  const showAlert = () => {
    alert('Sua busca deve conter somente 1 (um) caracter')
  }

  const [userEmail, setUserEmail] = useState('');
  const [searchRender, setSearchRender] = useState(false);
  const [filterSearch, setFilterSearch] = useState('');
  const [recipesFilters, setRecipesFilters] = useState([]);
  const [searchInput, setSearchInput] = useState('');
  const [getSearchBtn, setSearchBtn] = useState(false);

  useFetch(filterSearch , searchInput , setRecipesFilters ,getSearchBtn)

  const context = {
    filterSearch,
    userEmail,
    searchRender,
    searchInput,
    getSearchBtn,
    recipesFilters,
    setSearchBtn,
    setSearchInput,
    setFilterSearch,
    setUserEmail,
    setSearchRender,
  };

  return (
    <RecipesContext.Provider value={context}>
      {children}
    </RecipesContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
