import React, { useContext, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import perfilIcon from '../images/profileIcon.svg';
import search from '../images/searchIcon.svg';
import RecipesContext from '../context/RecipesContext';
import requestApi from '../services/functions';

function Header() {
  const [title, setTitle] = useState('Comidas')
  const [showButton, setShowButton] = useState(true)
  const { setSearchRender,
    searchRender,
    setFilterSearch,
    filterSearch,
    searchInput,
    setRecipesFilters,

  } = useContext(RecipesContext);

  const perfilRedirect = () => {
    history.push('/perfil');
  };
  const stateSearchInput = (stateInput) => {
    setSearchRender(!stateInput);
  };

  const handlerChange = ({ target }) => {
    setFilterSearch(target.value);
  };

  const searchButton = (filter, inputSearch, setFilter) => {
    requestApi(filter, inputSearch, setFilter);
  };

  const history = useHistory();
  const { location } = history
  const checkLocation = location.pathname
  switch (checkLocation) {
    case '/bebidas':
      setTitle('Bebidas');
      setShowButton(true);
    case '/explorar':
      setTitle('explorar');
      setShowButton(false);
    case '/explorar/comidas':
      setTitle('Explorar Comidas');
      setShowButton(false);
    case '/explorar/comidas/ingredientes':
      setTitle('Explorar ingredientes');
      setShowButton(false);
    case '/explorar/comidas/area':
      setTitle('Explorar Origem');
      setShowButton(false);
    default:
      setTitle('Comidas');

  }

  return (
    <header>
      <h1 data-testid="page-title">
        {title}
      </h1>
      <Link to="/perfil">
        <button type="button" onClick={perfilRedirect}>
          <img
            data-testid="profile-top-btn"
            src={perfilIcon}
            alt="perfil"
          />
        </button>
      </Link>
      <button hidden={showButton ? '' : 'hidden'} type="button" onClick={() => stateSearchInput(searchRender)}>
        <img
          data-testid="search-top-btn"
          src={search}
          alt="busca"
        />
      </button>

      <label htmlFor="ingredient">
        <input
          onClick={handlerChange}
          value="ingredients-input"
          data-testid="ingredient-search-radio"
          name="search"
          type="radio"
          id="ingredient"
        />
        Ingrediente
      </label>
      <label htmlFor="name">
        <input
          onClick={handlerChange}
          value="name-input"
          data-testid="name-search-radio"
          name="search"
          type="radio"
          id="name"
        />
        Nome
      </label>
      <label htmlFor="first">
        <input
          onClick={handlerChange}
          value="first-letter-input"
          data-testid="first-letter-search-radio"
          name="search"
          type="radio"
          id="first"
        />
        Primeira Letra
      </label>
      <button
        onClick={() => searchButton(filterSearch,
          searchInput,
          setRecipesFilters)}
        data-testid="exec-search-btn"
        type="button"
      >
        Buscar
      </button>
    </header>
  );
}

export default Header;
