import React, { useContext } from 'react';
import perfilIcon from '../images/profileIcon.svg';
import search from '../images/searchIcon.svg';
import history from '../history/history';
import RecipesContext from '../context/RecipesContext';

function Header() {
  const { setSearchRender, searchRender, setFilterSearch , setSearchBtn , getSearchBtn } = useContext(RecipesContext)

  const perfilRedirect = () => {
    history.push('/perfil')

  }
  const stateSearchInput = (stateInput) => {
    setSearchRender(!stateInput)
  }

  const handlerChange = ({ target }) => {
    setFilterSearch(target.value)
  }

  const searchButton = (stateButton) => {
     setSearchBtn(!stateButton)
  }

  return (
    <header>
      <h1 data-testid="page-title">Receitas de Comida</h1>
      <img onClick={perfilRedirect} data-testid="profile-top-btn" src={perfilIcon} alt="perfil" />
      <img data-testid="search-top-btn" onClick={() => stateSearchInput(searchRender)} src={search} alt="busca" />
      <label>
        <input onClick={handlerChange} value="ingredients-input" data-testid="ingredient-search-radio" name="search" type="radio" />
      Ingrediente
      </label>
      <label>
        <input onClick={handlerChange} value="name-input" data-testid="name-search-radio" name="search" type="radio" />
      Nome
      </label>
      <label>
        <input onClick={handlerChange} value="first-letter-input" data-testid="first-letter-search-radio" name="search" type="radio" />
      Primeira Letra
      </label>
      <button onClick={() => searchButton(getSearchBtn)} data-testid="exec-search-btn" >Buscar</button>
    </header>
  );
}

export default Header;
