import React, { useState , useEffect } from 'react';
import PropTypes from 'prop-types';
import RecipesContext from './RecipesContext';
import useRequestApi from '../hooks/useApi'


function Provider({ children }) {
  // const retrieveEmail = () => {
  // const storageEmail = JSON.parse(localStorage.getItem('user'));
  // return storageEmail === undefined ? '' : storageEmail.email;
  // };

  const [userEmail, setUserEmail] = useState('');
  const [searchRender, setSearchRender] = useState(false);
  const [filterSearch, setFilterSearch] = useState('');
  const [recipesFilters, setRecipesFilters] = useState([]);
  const [searchInput, setSearchInput] = useState('');
  const [getSearchBtn, setSearchBtn] = useState(false);



  useRequestApi(filterSearch , setRecipesFilters ,searchInput , getSearchBtn)




  const context = {
    filterSearch,
    userEmail,
    searchRender,
    searchInput,
    getSearchBtn,
    recipesFilters,
    setRecipesFilters,
    setSearchBtn,
    setSearchInput,
    setFilterSearch,
    setUserEmail,
    setSearchRender,
  };

  return (
    <RecipesContext.Provider value={context}>
      {children}
    </RecipesContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
