import React, { useContext, useEffect, useState } from 'react';
import { Redirect, Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Category from '../components/Category';
import SearchInput from '../components/SearchInput';
import RecipesContext from '../context/RecipesContext';
import { getCurrenceRecipesDrinksName } from '../services/drinkAPI';
import Loading from '../components/Loading'

function DrinksRecipes() {
  const [isFetching, setIsFetching] = useState(true)
  const zero = 0;
  const Twelve = 12;
  const {
    recipesFilters,
    setDrinkRecipeId,
    drinkRecipeId,
    setRecipesFilters,
    setInitialRecipes,
  } = useContext(RecipesContext);
  const filterRecipesTwelve = recipesFilters !== null
    ? recipesFilters
      .slice(zero, Twelve)
    : [];

  useEffect(() => {
    getCurrenceRecipesDrinksName('').then((response) => {
      setRecipesFilters(response.drinks);
      setInitialRecipes(response.drinks);
      setIsFetching(false)
    });
  }, [setRecipesFilters]);

  if (recipesFilters !== null && recipesFilters.length === 1) {
    const { idDrink } = recipesFilters[zero];
    setDrinkRecipeId(idDrink);
    return <Redirect to={`/bebidas/${idDrink}`} />;
  }

  if (drinkRecipeId !== '') {
    return <Redirect to={`/bebidas/${drinkRecipeId}`} />;
  }
  const showDetails = (id) => {
    setDrinkRecipeId(id);
  };

  const handleClick = (id) => {
    setDrinkRecipeId(id);
  };

  return (
    <div>
      <Header />
      <Category />
      {isFetching ? <Loading /> : filterRecipesTwelve.map((recipe, index) => (
        <button
          className="cards"
          type="button"
          onClick={() => showDetails(recipe.idDrink)}
          data-testid={`${index}-recipe-card`}
          key={index}
        >
          <p className="name-recipe" data-testid={`${index}-card-name`}>
            {recipe.strDrink}
          </p>
          <Link
            to={`/bebidas/${recipe.idDrink}`}
            onClick={() => handleClick(recipe.idDrink)}
          >
            <img
              className='img-cards'
              data-testid={`${index}-card-img`}
              width="163px"
              alt="receitas"
              src={recipe.strDrinkThumb}
            />
          </Link>
        </button>
      ))}
      <Footer />
    </div>
  );
}

export default DrinksRecipes;
