import React, { useContext } from 'react';
import perfilIcon from '../images/profileIcon.svg';
import search from '../images/searchIcon.svg';
import history from '../history/history';
import RecipesContext from '../context/RecipesContext';

function Header() {
  const { setSearchRender } = useContext(RecipesContext)

  const perfilRedirect = () => {
    history.push('/perfil')
  }
  const stateSearchInput = (stateInput) => {
    setSearchRender(!stateInput)
  }
  return (
    <header>
      <h1 data-testid="page-title">Receitas de Comida</h1>
      <img onClick={perfilRedirect} data-testid="profile-top-btn" src={perfilIcon} alt="perfil" />
      <img onClick={() => stateSearchInput(searchRender)} src={search} alt="busca" />
      <label>
        <input data-testid="ingredient-search-radio" name="search" type="radio" />
      Ingrediente
      </label>
      <label>
        <input data-testid="name-search-radio" name="search" type="radio" />
      Nome
      </label>
      <label>
        <input data-testid="first-letter-search-radio" name="search" type="radio" />
      Primeira Letra
      </label>
      <button data-testid="exec-search-btn" >Buscar</button>
    </header>
  );
}

export default Header;
