import React, { useState , useEffect } from 'react';
import PropTypes from 'prop-types';
import RecipesContext from './RecipesContext';
import getCurrenceRecipesFoods from '../services/foodAPI'


function Provider({ children }) {
  // const retrieveEmail = () => {
  // const storageEmail = JSON.parse(localStorage.getItem('user'));
  // return storageEmail === undefined ? '' : storageEmail.email;
  // };

  const [userEmail, setUserEmail] = useState('');
  const [searchRender, setSearchRender] = useState(false)
  const [ filterSearch , setFilterSearch ] = useState('');
  const [ recipesFilters , setRecipesFilters ] = useState([]);
  const [ searchInput  ,setSearchInput ] = useState('')

  const ingredients = `https://www.themealdb.com/api/json/v1/1/filter.php?i=${searchInput}`
  const name = `https://www.themealdb.com/api/json/v1/1/search.php?s=${searchInput}`
  const firstLetter = `https://www.themealdb.com/api/json/v1/1/search.php?f=${searchInput}`

  useEffect(() => {
    getCurrenceRecipesFoods()
  })

  const context = {
    filterSearch,
    userEmail,
    searchRender,
    searchInput,
    setSearchInput,
    setFilterSearch,
    setUserEmail,
    setSearchRender,
  };

  return (
    <RecipesContext.Provider value={ context }>
      {children}
    </RecipesContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
