import React, { useContext } from 'react';
import Header from '../components/Header';
import SearchInput from '../components/SearchInput';
import RecipesContext from '../context/RecipesContext';
import history from '../history/history';
function FoodRecipes() {
  const zero = 0;
  const Twelve = 12;
  const { searchRender, recipesFilters, setMealRecipeId } = useContext(RecipesContext);
  const filterRecipesTwelve = recipesFilters !== null
    ? recipesFilters.slice(zero, Twelve)
    : [];


    const showDetails = (id) => {
      setMealRecipeId(id)
      history.push(`/comidas/${id}`)
    }
  return (
    <div>
      <Header />
      { searchRender ? <SearchInput /> : null}
      {filterRecipesTwelve.map((recipe, index) => (
        <div onClick={() => showDetails(recipe.idMeal)  } data-testid={ `${index}-recipe-card` } key={ index }>
          <p data-testid={ `${index}-card-name` }>
            Nome:
            {recipe.strMeal}
          </p>
          <img
            data-testid={ `${index}-card-img` }
            width="200px"
            alt="receitas"
            src={ recipe.strMealThumb }
          />
        </div>
      ))}
    </div>
  );
}

export default FoodRecipes;
