import React, { useContext } from 'react';
import Header from '../components/Header';
import SearchInput from '../components/SearchInput';
import RecipesContext from '../context/RecipesContext';

function DrinksRecipes() {
  const zero = 0;
  const Twelve = 12;
  const { searchRender, recipesFilters } = useContext(RecipesContext);
  const filterRecipesTwelve = 
  recipesFilters !== null ? recipesFilters.slice(zero, Twelve) : [];

  return (
    <div>
      <Header />
      { searchRender ? <SearchInput /> : null}

      {filterRecipesTwelve.map((recipe, index) => (
        <div data-testid={ `${index}-recipe-card` } key={ index }>
          <p data-testid={ `${index}-card-name` }>
            Nome:
            {recipe.strDrink}
          </p>
          <img
          data-testid={ `${index}-card-img` }
          width="200px" alt="receitas"
          src={ recipe.strDrinkThumb } />
        </div>
      ))}
    </div>
  );
}

export default DrinksRecipes;
