import React, { useContext } from 'react';
import { Redirect , Link } from 'react-router-dom';
import Header from '../components/Header';
import SearchInput from '../components/SearchInput';
import RecipesContext from '../context/RecipesContext';

function DrinksRecipes() {
  const zero = 0;
  const Twelve = 12;
  const { searchRender, recipesFilters, setDrinkRecipeId } = useContext(RecipesContext);
  const filterRecipesTwelve = recipesFilters !== null
    ? recipesFilters.slice(zero, Twelve)
    : [];

  if (recipesFilters !== null && recipesFilters.length === 1) {
    const { idDrink } = recipesFilters[zero];
    setDrinkRecipeId(idDrink);
    return <Redirect to={ `bebidas/${idDrink}` } />;
  }

  const showDetails = (id) => {
    setDrinkRecipeId(id)
    return <Redirect to={`bebidas/${id}`} />
  }

  return (
    <div>
      <Header />
      { searchRender ? <SearchInput /> : null}

      {filterRecipesTwelve.map((recipe, index) => (
        <Link data-testid={ `${index}-recipe-card` } key={ index }>
          <p data-testid={ `${index}-card-name` }>
            Nome:
            {recipe.strDrink}
          </p>
          <img
            data-testid={ `${index}-card-img` }
            width="200px"
            alt="receitas"
            src={ recipe.strDrinkThumb }
          />
        </Link>
      ))}
    </div>
  );
}

export default DrinksRecipes;
